(ns adventofcode2016.day2)

(def input "



")

;; Part 1
#_
(def keypad [[1 2 3]
             [4 5 6]
             [7 8 9] ])

;; Part 2
(def keypad [[nil nil  1 nil nil]
             [nil   2  3   4 nil]
             [  5   6  7   8   9]
             [nil  \A \B  \C nil]
             [nil nil \D nil nil] ])

(defn in-bounds? [location]
  (get-in keypad location) )

(def directions {\U [-1  0]
                 \R [ 0  1]
                 \D [ 1  0]
                 \L [ 0 -1] })

(defn process-instruction [[location-row location-column :as location] direction]
  (let [[direction-row direction-column] (directions direction)
        potential-new-location [(+ direction-row    location-row)
                                (+ direction-column location-column) ] ]
    (if (in-bounds? potential-new-location)
      potential-new-location
      location ) ) )

(defn process-line [{:keys [location code]} line]
  (let [new-location (reduce process-instruction location line)]
    {:location new-location
     :code (str code (get-in keypad new-location)) } ) )

(defn process-lines [lines]
  (reduce process-line {:location [2 0] :code ""} lines) )

(defn -main []
  (-> input
      clojure.string/split-lines
      process-lines ) )
